option.value:
import React from "react";
import type { LogoCarouselBlock as LogoCarouselProps, Media as MediaType } from "@/payload-types";
import { Media } from "@/components/Media";
import { cn } from "@/utilities/ui";

/**
 * Continuous marquee-style logo carousel:
 * - Duplicates the logo list to create a seamless loop.
 * - Uses CSS animation for infinite horizontal scroll.
 * - When one set exits left, the duplicate follows, forming a ring.
 */
export const LogoCarousel: React.FC<LogoCarouselProps> = ({ logos, speed = 30, gap = 24 }) => {
  const items = (logos || []).map((l) => ({
    image: typeof l?.image === "object" ? (l.image as MediaType) : null,
    alt: l?.alt ?? "",
  })).filter((i) => i.image);

  // Duplicate items to ensure seamless loop even if few logos
  const loopItems = [...items, ...items];

  // Animation duration derived from speed (seconds per loop)
  const duration = Math.max(speed, 5);

  return (
    <section className={cn("relative w-full overflow-hidden")}> 
      <div
        className={cn(
          "flex w-max",
          "gap-6", // base gap; overridden inline by style
          "animate-[marquee_linear_infinite]"
        )}
        style={{
          // custom CSS vars consumed in globals.css utility (we add the keyframes below if needed)
          // fallback to px gap; tailwind class still applies
          columnGap: `${gap}px`,
          animationDuration: `${duration}s`,
        }}
      >
        {loopItems.map((item, idx) => (
          <div key={idx} className={cn("shrink-0 flex items-center justify-center")}> 
            <div className={cn("h-38 w-38 md:h-64 md:w-64")}> 
              <Media
                resource={item.image as any}
                pictureClassName="h-full w-full"
                imgClassName="h-full w-full object-contain"
                fill
              />
            </div>
          </div>
        ))}
      </div>
    </section>
  );
};
